mapper.update:
update t_photo_online set yn = 0
where
order_id = #{orderId}

update t_order set is_photo_online = 1
where
id = #{orderId}

mapper.select:
select t2.id,t1.id as orderId,t2.is_approval as isApproval,t2.cover_img as coverImg,t2.banner_img as bannerImg,t2.live_name as liveName, t2.access_num as accessNum,t2.forward_num as forwardNum,t2.open_auth as openAuth,access_pwd as accessPwd, (select ls.`journey_name` from line_sight ls where ls.order_id=t1.id limit 1) as address, (CASE WHEN t2.is_approval = 1 THEN (select count(*) from t_shoot_group tsg inner join t_group_photo tgp on tsg.id = tgp.group_id where tsg.order_no = t1.order_no and tgp.yn = 1 and tgp.is_delete = 1 and tgp.update_url is not NULL) ELSE (select count(*) from t_shoot_group tsg inner join t_group_photo tgp on tsg.id = tgp.group_id where tsg.order_no = t1.order_no and tgp.yn = 1 and tgp.is_delete = 1) END )AS photoNum, (case when open_time = 2 then concat(date_format(t2.start_time,'%Y-%m-%d'),'~',date_format(t2.end_time,'%Y-%m-%d')) when t3.shoot_time is null then date_format(t1.start_time,'%Y-%m-%d') else date_format(t3.shoot_time,'%Y-%m-%d') end) as startTime from t_order t1 inner join t_photo_online t2 on t1.id = t2.order_id left join (select `journey_name`,shoot_time,order_id from line_sight limit 1) t3 on t3.order_id = t1.id where t1.is_photo_online = 1 and t2.yn = 1 and t1.status <>3 and ( (t2.open_time = 2 AND DATE_FORMAT(t2.start_time,'%Y-%m-%d') <= DATE_FORMAT(now(),'%Y-%m-%d') AND DATE_FORMAT(t2.end_time,'%Y-%m-%d') >= DATE_FORMAT(now(),'%Y-%m-%d')) or open_time = 1)
and t2.live_type_id = #{liveTypeId}
order by t2.created_time DESC

mapper.update:
update t_photo_online set access_num = access_num + 1
where
order_id = #{orderId}
